import requests

class FilePicker():
    def getFileContents(fpurl):
        """Get the contents of the file pointed to by fpurl"""
        r = requests.get(fpurl)
        return r.content

    def saveDataToFile(fpurl, data):
        """Save data to the file pointed to by fpurl"""
        r = requests.put(fpurl, data=data)
        if r.status_code == requests.codes.ok:
            return True
        else:
            return False
